        /// <summary>
        /// Método responsável por escrever as respostas na tela
        /// </summary>
        /// <param name="textoEmIValueshDesejado">Texto em ivaluesh em que deseja encontrar as respostas</param>
        private static void EscreverRespostasNaTela(DtoTextoIValuesh textoEmIValuesh)
        {
            //QUESTÃO 1 – CONTAR PREPOSIÇÕES
            
            //Quantidade de preposições no texto B é igual a 22
            Console.WriteLine("QUESTÃO 1 – CONTAR PREPOSIÇÕES\nResposta:\nQuantidade de preposições no texto {0}: {1}", textoEmIValuesh.Nome, BlIValuesh.ObterQuantidadePreposicoesNoTexto(textoEmIValuesh.Texto));
            
            //FIM QUESTÃO 1


            //QUESTÃO 2 – CONTAR VERBOS

            //Obtenho os verbos no texto 
            var verbosTexto = BlIValuesh.ObterVerbosNoTexto(textoEmIValuesh.Texto);

            //Obtenho a quantidade de verbos no texto 
            var qtdVerbosTexto = BlIValuesh.ObterQuantidadeDeVerbosNoTexto(verbosTexto);

            //Obtenho a quantidade de verbos em primeira pessoa no texto 
            var qtdVerbosTextoPrimeiraPessoa = BlIValuesh.ObterQuantidadeDeVerbosNoTextoEmPrimeiraPessoa(verbosTexto);

            //A quantidade de verbos no texto B é igual a 22
            Console.WriteLine("\n\nQUESTÃO 2 – CONTAR VERBOS\nRespostas:\nQuantidade de verbos no texto {0}: {1}", textoEmIValuesh.Nome, qtdVerbosTexto);

            //A quantidade de verbos em primeira pessoa no texto B é igual a 8
            Console.WriteLine("Quantidade de verbos em primeira pessoa no texto {0}: {1}", textoEmIValuesh.Nome, qtdVerbosTextoPrimeiraPessoa);

            //FIM QUESTÃO 2


            Console.Read();
